INSERT INTO `news` (`news_id`, `title`, `subtitle`, `news_description`, `image`, `publishing_date`, `expiry_date`, `is_active`, `user_id`) VALUES
(1, 'Лятото на 2016 г.: Руснаците се връщат', 'Заради кризата със сваления самолет нашето Черноморие ще поеме част туристите, планирали почивка в Турция', 'Следващото лято руските туристи ще се завърнат по българското Черноморие. Това сочат прогнозите на туристическия бранш в Русия във връзка с проблемите й с Турция покрай сваления вчера над Сирия бомбардировач. Така, след като през тази година отчетохме сериозен спад на туристическия поток от Русия, се очертава догодина да си върнем загубеното и дори да спечелим още повече.\r\n<br/>\r\nПрез 2016 г. руските и средиземноморските курорти ще бъдат най-предпочитаните за туристите, разказва пред ТАСС изпълнителният директор на Уралската туристическа асоциация Михаил Малцев. Той уточнява обаче, че там все пак местата са ограничени. Така нашата страна отново ще се върне сред основните фокуси за руските туристи.\r\n<br/>\r\n"В момента очакваме точни указания от Федералната агенция по туризъм. Ако бъдат въведени ограничения на полетите до Турция и през това време не бъдат възстановени тези до Египет, тогава приоритетни дестинации за летните почивки на хората ще станат със сигурност руските курорти – Краснодарския край и Крим. България също е сред потенциалните лидери, тъй като условията там много приличат на тези в Турция. Освен това водим преговори и с Гърция за възможностите да настаним там част от туристите", обяснява Малцев.\r\n<br/>\r\nТой отбелязва още, че сред курортните дестинации на руските туристи през следващото лято ще останат и Италия, Испания, Хърватия и Черна гора. Почивките в тези страни обаче са по-скъпи, макар и да предлагат в пакетите не само морски, но и исторически и културен туризъм, обяснява Малцев.\r\n<br/><br/>\r\nИзточник: inews.bg', 'news-default-img', '2015-11-30 08:34:05', '2016-02-26', 1, 2),
(2, 'Туристическият бизнес притеснен от липсата на сняг и топлото време', 'И тази зима хотелиерите разчитат на гръцкия, турския и румънския пазар', 'Представители на туристическия бизнес са изразили умерен оптимизъм за предстоящия зимен сезон. Хотелиерите са притеснени основно от топлото време и закъсняването на снега. Това съобщи зам.-министърът на туризма Ирена Георгиева по време на туристическото изложение "Go2Balkans".\r\n"Всички са притеснени от топлото време, но като цяло записванията вървят добре. Разчита се на засилени записвания в последния момент", увери заместничката на Николина Ангелкова.\r\n<br/>\r\nМинистерството разчита на ръст на зимните туристи от Израел, Молдова, Великобритания, Словакия, Македония и на традиционните балкански пазари - Гърция, Румъния и Турция.\r\n<br/>\r\n"Всъщност гръцките, турските и румънските туристи са сред генериращите най-много приходи за българския входящ туризъм", констатира зам.-министърът. За 2014 г. туристите от Гърция в България са били над 1 милион, от Румъния - малко над половин милион, а от Турция - малко под половин милион.\r\n<br/>\r\nГеоргиева няма впечатления кризата в Гърция да се е отразила на туристическите пътувания на гърците през лятото. Тя се надява и предстоящият зимен сезон да не бъде засегнат от проблемите в южната ни съседка.\r\n<br/>\r\nМинистерството на туризма ще подкрепя всяка инициатива за създаване на общи туристически балкански продукти, каза още зам.-министърът. Такава възможност имало в рамките на Дунавската стратегия и в частност в разработването на културно-историческите маршрути.\r\n<br/>\r\n"Работим усилено за създаването на бранд Дунав", увери тя. България и Румъния имат подписан меморандум за сътрудничество и напреднали отношения по повод изграждането на общ туристически център в Китай. С Гърция пък се водят разговори за създаване на подобни общи продукти и средства за налагане на региона на Балканите като туристическа дестинация.\r\n<br/>\r\nКитайските туристи се интересуват от минимум три дестинации при едно пътуване, което е не по-кратко от десет дни до две седмици, посочи ресорният зам.-министър.\r\n<br/><br/>\r\nИзточник: inews.bg', 'news-default-img', '2015-12-01 08:34:05', '2016-06-22', 1, 2),
(3, 'Българска забележителност е сред топ дестинациите на планетата.', 'В класацията има и други места от Балканите', 'Рилският манастир влезе в класацията  за най-добрите места на планетата, които всеки трябва да посети.\r\n<br/>\r\nТуристическият пътеводител Lonely Planet обяви класацията си "500-те топ дестинации на планетата", подбрани след години на проучване. Сред избраните места попада и Рилският манастир.\r\n<br/>\r\nВ класацията има и други места от Балканите. Номер 10 е църквата "Св. София” в Истанбул, включени са също дворецът "Топкапъ”, Синята джамия, Ефес, Кападокия и Памуккале в Турция, манастирите Метеора в Гърция, Акрополът и още няколко обекта от региона.\r\n<br/>\r\n"Местата, които всеки трябва да види, преди да умре” е посланието на Lonely Planet, който ще публикува снимки и информация за Рилския манастир в специално издание с подбраните "500 топ дестинации на планетата”.\r\n<br/><br/>\r\nИзточник: inews.bg', 'news-default-img', '2015-11-30 08:36:41', '2016-06-22', 1, 2),
(4, 'Пътуваме по-малко у нас и в чужбина - 8% спад за третото тримесечие', 'Все повече българи го правят самостоятелно, без услугите на туроператори.', 'Българите са пътували със 7,7% по-малко в страната и в чужбина за третото тримесечие спрямо същия период на миналата година. В традиционно най-натоварените туристически месеци – юли, август и септември, пътувания с цел екскурзия и почивка са осъществили само Общо 1 274 7000 българи, навършили 15 години, показват данните на Националния статистически институт.\r\n<br/>\r\n80,5% са пътували само в страната, 14,6% - само в чужбина, а 4,9% от сънародниците са излизали у нас и зад граница. За тримесечието е отчетено намаление от 8,7% на пътешествалите в България, а на излизанията в чужбина - с 1,4%.\r\n<br/>\r\nНай-много туристически пътувания са осъществили хората между 25 и 44 години (47,4%), воайажите на 83,8% от тях са били предимно в родината. Най-много посещения в чужбина за този период пък са напарвили българите между 15 и 24 години или 18,5%.\r\n<br/>\r\nПо-голямата част от туристическите пътувания на лицата на възраст от 15 и повече навършени години както в страната, така и в чужбина, са били с цел почивка и екскурзия - съответно 72,9% и 64,7%.\r\n<br/>\r\n1 121 200 от пътуванията са били самостоятелни – без използване на туристически услуги, което е 89,9% от общия брой лични пътувания. Относителният дял на самостоятелните пътувания без резервация в страната е 94,0%, а на тези в чужбина – 66,4%. Делът на личните пътувания, организирани чрез туристическа агенция или туроператор в чужбина, е 33,6%, а в страната – само 6%.\r\n<br/>\r\nРазходите за храна имат най-голям при пътуванията ни в страната и в чужбина – съответно 45,5% и 32,9%. През третото тримесечие на 2015 г. средният разход при пътуване с лична цел на едно лице на възраст над 15 години е 291,24 лв. - у нас и 489,02 лв. - зад граница. Разходите на служител в командировка в страната пък са съответно 242,38 лв. в страната и 1030,48 лв. в чужбина, сочат данните на статистиката.\r\n<br/><br/>\r\nИзточник: inews.bg', 'news-default-img', '2015-11-30 08:36:41', '2016-07-14', 1, 2);
